import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "animate.css";
import "./App.css";

const ExamPaperGenerator = () => {
  const [examType, setExamType] = useState("");
  const [topics, setTopics] = useState([]);
  const [difficulty, setDifficulty] = useState("");
  const [totalMarks, setTotalMarks] = useState("");
  const [isGenerating, setIsGenerating] = useState(false);
  const [animatedIndex, setAnimatedIndex] = useState(0);

  const allTopics = [
    "Dynamic Programming", "Graphs", "Trees", "Arrays", "Linked Lists",
    "Stacks", "Queues", "Heaps", "Sorting", "Searching",
    "Recursion", "Bit Manipulation", "Greedy Algorithms", "Backtracking", "Divide & Conquer"
  ];

  // Animation for cards on page load
  useEffect(() => {
    const timer = setInterval(() => {
      if (animatedIndex < 4) {
        setAnimatedIndex(prev => prev + 1);
      } else {
        clearInterval(timer);
      }
    }, 200);
    return () => clearInterval(timer);
  }, [animatedIndex]);

  const handleTopicChange = (event) => {
    const { value, checked } = event.target;
    setTopics((prev) =>
      checked ? [...prev, value] : prev.filter((topic) => topic !== value)
    );
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsGenerating(true);
    // Simulate generation process
    setTimeout(() => {
      setIsGenerating(false);
      alert(`Exam Type: ${examType}\nTopics: ${topics.join(", ")}\nDifficulty: ${difficulty}\nTotal Marks: ${totalMarks}`);
    }, 2000);
  };

  // Get animation class for each section based on index
  const getAnimationClass = (index) => {
    if (index <= animatedIndex) {
      return "animate__animated animate__fadeInUp";
    }
    return "opacity-0";
  };

  return (
    <div className="container my-5">
      <div className="bg-gradient-primary p-5 rounded-lg shadow-lg animate__animated animate__zoomIn" 
           style={{ 
             background: "linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)",
             borderRadius: "20px" 
           }}>
        <h1 className="text-center mb-4 text-white fw-bold animate__animated animate__pulse animate__infinite animate__slow">
          <i className="fas fa-graduation-cap me-2"></i>
          Exam Paper Generator
        </h1>

        {isGenerating ? (
          <div className="text-center p-5">
            <div className="spinner-border text-light" style={{ width: "3rem", height: "3rem" }} role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <h3 className="text-white mt-3 animate__animated animate__flash animate__infinite">Generating your exam paper...</h3>
          </div>
        ) : (
          <form onSubmit={handleSubmit}>
            {/* Exam Type */}
            <div className={`card p-4 mb-4 shadow-lg border-0 ${getAnimationClass(0)}`} style={{ borderRadius: "15px" }}>
              <h4 className="form-label fw-bold mb-3 text-primary">
                <i className="fas fa-file-alt me-2"></i>Exam Type
              </h4>
              <div className="d-flex flex-wrap gap-3">
                {['Theory', 'Practical', 'Technical Round'].map(type => (
                  <div key={type} className="form-check custom-radio">
                    <input
                      id={`type-${type}`}
                      type="radio"
                      name="examType"
                      value={type}
                      className="form-check-input"
                      onChange={(e) => setExamType(e.target.value)}
                    />
                    <label className="form-check-label btn btn-outline-primary px-4 py-2" htmlFor={`type-${type}`}>
                      {type}
                    </label>
                  </div>
                ))}
              </div>
            </div>

            {/* Topics */}
            <div className={`card p-4 mb-4 shadow-lg border-0 ${getAnimationClass(1)}`} style={{ borderRadius: "15px" }}>
              <h4 className="form-label fw-bold mb-3 text-primary">
                <i className="fas fa-list-alt me-2"></i>Topics
              </h4>
              <div className="row g-3">
                {allTopics.map((topic, index) => (
                  <div key={topic} className="col-md-4 col-sm-6">
                    <div className="form-check custom-checkbox animate__animated animate__fadeIn" style={{ animationDelay: `${index * 0.05}s` }}>
                      <input
                        id={`topic-${topic}`}
                        type="checkbox"
                        value={topic}
                        className="form-check-input"
                        onChange={handleTopicChange}
                      />
                      <label className="form-check-label btn btn-outline-info w-100 text-start" htmlFor={`topic-${topic}`}>
                        {topic}
                      </label>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Difficulty Level */}
            <div className={`card p-4 mb-4 shadow-lg border-0 ${getAnimationClass(2)}`} style={{ borderRadius: "15px" }}>
              <h4 className="form-label fw-bold mb-3 text-primary">
                <i className="fas fa-tachometer-alt me-2"></i>Difficulty
              </h4>
              <div className="difficulty-slider d-flex justify-content-between gap-3">
                {['Easy', 'Medium', 'Hard'].map(level => (
                  <div key={level} className="form-check flex-fill">
                    <input
                      id={`diff-${level}`}
                      type="radio"
                      name="difficulty"
                      value={level}
                      className="form-check-input"
                      onChange={(e) => setDifficulty(e.target.value)}
                    />
                    <label 
                      className={`form-check-label btn w-100 py-3 ${level === 'Easy' ? 'btn-outline-success' : level === 'Medium' ? 'btn-outline-warning' : 'btn-outline-danger'}`} 
                      htmlFor={`diff-${level}`}
                    >
                      {level}
                      {level === 'Easy' && <i className="ms-2 fas fa-smile"></i>}
                      {level === 'Medium' && <i className="ms-2 fas fa-meh"></i>}
                      {level === 'Hard' && <i className="ms-2 fas fa-dizzy"></i>}
                    </label>
                  </div>
                ))}
              </div>
            </div>

            {/* Total Marks */}
            <div className={`card p-4 mb-4 shadow-lg border-0 ${getAnimationClass(3)}`} style={{ borderRadius: "15px" }}>
              <h4 className="form-label fw-bold mb-3 text-primary">
                <i className="fas fa-star me-2"></i>Total Marks
              </h4>
              <div className="d-flex justify-content-between gap-3">
                {['50', '70', '100'].map(marks => (
                  <div key={marks} className="form-check flex-fill">
                    <input
                      id={`marks-${marks}`}
                      type="radio"
                      name="totalMarks"
                      value={marks}
                      className="form-check-input"
                      onChange={(e) => setTotalMarks(e.target.value)}
                    />
                    <label className="form-check-label btn btn-outline-primary w-100 py-3 position-relative" htmlFor={`marks-${marks}`}>
                      <span className="fs-4 fw-bold">{marks}</span>
                      <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger animate__animated animate__bounce animate__infinite">
                        {marks === '100' && 'MAX'}
                      </span>
                    </label>
                  </div>
                ))}
              </div>
            </div>

            <div className={getAnimationClass(4)}>
              <button 
                type="submit" 
                className="btn btn-lg w-100 p-3 shadow-lg animate__animated animate__heartBeat animate__infinite animate__slow"
                style={{ 
                  background: "linear-gradient(90deg, #FF416C 0%, #FF4B2B 100%)",
                  borderRadius: "15px",
                  border: "none",
                  color: "white",
                  fontWeight: "bold",
                  fontSize: "1.2rem"
                }}
              >
                <i className="fas fa-magic me-2"></i>
                Generate Exam Paper
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default ExamPaperGenerator;
