import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const ExamPaperGenerator = () => {
  const [examType, setExamType] = useState("");
  const [topics, setTopics] = useState([]);
  const [difficulty, setDifficulty] = useState("");
  const [totalMarks, setTotalMarks] = useState("");

  const handleTopicChange = (event) => {
    const { value, checked } = event.target;
    setTopics((prev) =>
      checked ? [...prev, value] : prev.filter((topic) => topic !== value)
    );
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`Exam Type: ${examType}\nTopics: ${topics.join(", ")}\nDifficulty: ${difficulty}\nTotal Marks: ${totalMarks}`);
  };

  return (
    <div className="container mt-5 p-4 bg-light rounded shadow animated fadeIn">
      <h2 className="text-center mb-4">Exam Paper Generator</h2>
      <form onSubmit={handleSubmit}>
        {/* Exam Type */}
        <div className="mb-3">
          <label className="form-label fw-bold">Exam Type:</label>
          {['Theory', 'Practical', 'Technical Round'].map(type => (
            <div key={type} className="form-check">
              <input
                type="radio"
                name="examType"
                value={type}
                className="form-check-input"
                onChange={(e) => setExamType(e.target.value)}
              />
              <label className="form-check-label">{type}</label>
            </div>
          ))}
        </div>

        {/* Topics */}
        <div className="mb-3">
          <label className="form-label fw-bold">Topics:</label>
          {['Dynamic Programming', 'Graphs', 'Trees'].map(topic => (
            <div key={topic} className="form-check">
              <input
                type="checkbox"
                value={topic}
                className="form-check-input"
                onChange={handleTopicChange}
              />
              <label className="form-check-label">{topic}</label>
            </div>
          ))}
        </div>

        {/* Difficulty Level */}
        <div className="mb-3">
          <label className="form-label fw-bold">Difficulty:</label>
          {['Easy', 'Medium', 'Hard'].map(level => (
            <div key={level} className="form-check">
              <input
                type="radio"
                name="difficulty"
                value={level}
                className="form-check-input"
                onChange={(e) => setDifficulty(e.target.value)}
              />
              <label className="form-check-label">{level}</label>
            </div>
          ))}
        </div>

        {/* Total Marks */}
        <div className="mb-3">
          <label className="form-label fw-bold">Total Marks:</label>
          {['50', '70', '100'].map(marks => (
            <div key={marks} className="form-check">
              <input
                type="radio"
                name="totalMarks"
                value={marks}
                className="form-check-input"
                onChange={(e) => setTotalMarks(e.target.value)}
              />
              <label className="form-check-label">{marks}</label>
            </div>
          ))}
        </div>

        <button type="submit" className="btn btn-primary w-100">Generate Paper</button>
      </form>
    </div>
  );
};

export default ExamPaperGenerator;
